// src/components/CalendarPage.jsx (‰øÆÂ§çÁâà)
import React, { useState, useEffect } from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import { useNavigate } from 'react-router-dom';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import './CalendarPage.css';
import { storageService } from '../utils/storage';
import ImageSearchModal from './ImageSearchModal';

// ËÆæÁΩÆ‰∏≠ÊñáÊú¨Âú∞Âåñ
moment.locale('zh-cn');
const localizer = momentLocalizer(moment);

const CalendarPage = () => {
  const navigate = useNavigate();
  const [currentDate, setCurrentDate] = useState(new Date());
  const [view, setView] = useState('month');
  const [events, setEvents] = useState([]);
  const [hoveredDate, setHoveredDate] = useState(null);
  const [hoveredPosition, setHoveredPosition] = useState({ x: 0, y: 0 });

  // Êñ∞Â¢ûÁä∂ÊÄÅ
  const [showImageSearch, setShowImageSearch] = useState(false);
  const [selectedDate, setSelectedDate] = useState(null);

  useEffect(() => {
    const savedEvents = storageService.getEvents();

    if (savedEvents.length === 0) {
      const currentYear = new Date().getFullYear();
      const currentMonth = new Date().getMonth();
      const currentDay = new Date().getDate();

      const sampleEvents = [
        {
          id: 1,
          title: 'Âõ¢ÈòüÂë®‰ºö',
          start: new Date(currentYear, currentMonth, currentDay, 10, 0),
          end: new Date(currentYear, currentMonth, currentDay, 11, 30),
          type: 'work'
        },
        {
          id: 2,
          title: 'È°πÁõÆËØÑÂÆ°',
          start: new Date(currentYear, currentMonth, currentDay + 1, 14, 0),
          end: new Date(currentYear, currentMonth, currentDay + 1, 16, 0),
          type: 'meeting'
        },
        {
          id: 3,
          title: 'ÂÅ•Ë∫´ËÆ≠ÁªÉ',
          start: new Date(currentYear, currentMonth, currentDay, 18, 0),
          end: new Date(currentYear, currentMonth, currentDay, 19, 0),
          type: 'personal'
        },
        {
          id: 4,
          title: 'ÂÆ¢Êà∑‰ºöËÆÆ',
          start: new Date(currentYear, currentMonth, currentDay - 1, 9, 0),
          end: new Date(currentYear, currentMonth, currentDay - 1, 10, 30),
          type: 'urgent'
        }
      ];

      sampleEvents.forEach(event => storageService.addEvent(event));
      setEvents(sampleEvents);
    } else {
      const processedEvents = savedEvents.map(event => ({
        ...event,
        start: new Date(event.start),
        end: new Date(event.end)
      }));
      setEvents(processedEvents);
    }
  }, []);

  const navigateDate = (direction) => {
    let newDate;
    if (view === 'month') {
      newDate = moment(currentDate).add(direction, 'months').toDate();
    } else if (view === 'week') {
      newDate = moment(currentDate).add(direction, 'weeks').toDate();
    } else {
      newDate = moment(currentDate).add(direction, 'days').toDate();
    }
    setCurrentDate(newDate);
  };

  const goToToday = () => {
    setCurrentDate(new Date());
  };

  const handleViewChange = (newView) => {
    setView(newView);
  };

  // ‰∫ã‰ª∂Ê†∑Âºè
  const eventStyleGetter = (event) => {
    let backgroundColor = '#3174ad';

    switch (event.type) {
      case 'work':
        backgroundColor = '#3498db';
        break;
      case 'meeting':
        backgroundColor = '#9b59b6';
        break;
      case 'personal':
        backgroundColor = '#2ecc71';
        break;
      case 'urgent':
        backgroundColor = '#e74c3c';
        break;
      default:
        backgroundColor = '#3174ad';
    }

    return {
      style: {
        backgroundColor,
        borderRadius: '4px',
        opacity: 0.9,
        color: 'white',
        border: 'none',
        fontSize: '12px',
        fontWeight: '500',
        padding: '2px 4px'
      }
    };
  };

  // ‰∫ã‰ª∂Â§ÑÁêÜÂáΩÊï∞
  const handleSelectEvent = (event) => {
    alert(`‰∫ã‰ª∂: ${event.title}\nÂºÄÂßã: ${moment(event.start).format('YYYY-MM-DD HH:mm')}\nÁªìÊùü: ${moment(event.end).format('YYYY-MM-DD HH:mm')}`);
  };

  // Êó•ÊúüÊÇ¨ÂÅúÂ§ÑÁêÜ
  const handleDayMouseEnter = (date, e) => {
    const dayEvents = storageService.getEventsByDate(date);

    if (dayEvents.length > 0) {
      setHoveredDate({ date, events: dayEvents });
      setHoveredPosition({ x: e.clientX, y: e.clientY });
    }
  };

  const handleDayMouseLeave = () => {
    setHoveredDate(null);
  };

  // Êó•ÊúüÁÇπÂáªÂ§ÑÁêÜ - Ë∑≥ËΩ¨Âà∞ËØ¶ÊÉÖÈ°µ
  const handleSelectSlot = ({ start, end }) => {
    const dateStr = moment(start).format('YYYY-MM-DD');
    navigate(`/day/${dateStr}`);
  };

  const handleAddEvent = () => {
    const title = prompt('ËØ∑ËæìÂÖ•‰∫ã‰ª∂Ê†áÈ¢ò:');
    if (!title) return;

    const type = prompt('ËØ∑ËæìÂÖ•‰∫ã‰ª∂Á±ªÂûã (work/meeting/personal/urgent):', 'work');
    const startTime = prompt('ËØ∑ËæìÂÖ•ÂºÄÂßãÊó∂Èó¥ (HH:mm)', '09:00');
    const endTime = prompt('ËØ∑ËæìÂÖ•ÁªìÊùüÊó∂Èó¥ (HH:mm)', '10:00');

    const today = new Date();
    const [startHour, startMinute] = startTime.split(':');
    const [endHour, endMinute] = endTime.split(':');

    const newEvent = {
      title,
      start: new Date(today.getFullYear(), today.getMonth(), today.getDate(), startHour, startMinute),
      end: new Date(today.getFullYear(), today.getMonth(), today.getDate(), endHour, endMinute),
      type: type || 'work'
    };

    const savedEvent = storageService.addEvent(newEvent);
    const eventWithDateObjects = {
      ...savedEvent,
      start: new Date(savedEvent.start),
      end: new Date(savedEvent.end)
    };
    setEvents(prev => [...prev, eventWithDateObjects]);
  };

  // Êñ∞Â¢ûÔºöÂ§ÑÁêÜÂõæÁâáÊêúÁ¥¢
  const handleImageSearch = (date) => {
    setSelectedDate(date);
    setShowImageSearch(true);
  };

  // Êñ∞Â¢ûÔºöÂ§ÑÁêÜÂõæÁâáÈÄâÊã©ÔºàËÆæÁΩÆ‰∏∫ËÉåÊôØÔºâ
  const handleImageSelect = (imageUrl) => {
    if (selectedDate) {
      storageService.saveDailyBackground(selectedDate, imageUrl);
      alert('ËÉåÊôØÂõæÁâáËÆæÁΩÆÊàêÂäüÔºÅ');
    }
  };

  // Êñ∞Â¢ûÔºöÂ§ÑÁêÜÂõæÁâá‰øùÂ≠ò
  const handleImageSave = (imageData) => {
    if (selectedDate) {
      storageService.saveDailyImage(selectedDate, imageData);
      // Ëß¶ÂèëÈáçÊñ∞Ê∏≤Êüì‰ª•Êõ¥Êñ∞Ê†áËÆ∞
      setEvents([...events]);
    }
  };

  // Ê∏≤ÊüìÊÇ¨ÂÅúÈ¢ÑËßàÂç°Áâá - ‰øÆÂ§çÔºöÁßªÂà∞Ê≠£Á°Æ‰ΩçÁΩÆ
  const renderHoverPreview = () => {
    if (!hoveredDate) return null;

    return (
      <div 
        className="date-preview-card"
        style={{
          position: 'fixed',
          left: hoveredPosition.x + 10,
          top: hoveredPosition.y + 10,
          zIndex: 1000,
          pointerEvents: 'none'
        }}
      >
        <div className="preview-header">
          <h4>{moment(hoveredDate.date).format('MMÊúàDDÊó•')}</h4>
          <span className="event-count">{hoveredDate.events.length} ‰∏™‰∫ã‰ª∂</span>
        </div>
        <div className="preview-events">
          {hoveredDate.events.map(event => (
            <div key={event.id} className={`preview-event ${event.type}`}>
              <div className="event-time">
                {moment(event.start).format('HH:mm')}
              </div>
              <div className="event-title">{event.title}</div>
            </div>
          ))}
        </div>
      </div>
    );
  };

  // Êõ¥Êñ∞ÔºöËá™ÂÆö‰πâÊó•ÊúüÂçïÂÖÉÊ†ºÂåÖË£ÖÂô® - Ê∑ªÂä†ÂõæÁâáÊ†áËÆ∞
  const CustomDayWrapper = ({ children, value }) => {
    const hasEvents = storageService.getEventsByDate(value).length > 0;
    const hasImages = storageService.hasDailyImages(moment(value).format('YYYY-MM-DD'));

    return (
      <div 
        onMouseEnter={(e) => handleDayMouseEnter(value, e)}
        onMouseLeave={handleDayMouseLeave}
        onClick={() => handleSelectSlot({ start: value, end: value })}
        onContextMenu={(e) => {
          e.preventDefault();
          handleImageSearch(moment(value).format('YYYY-MM-DD'));
        }}
        style={{ 
          cursor: 'pointer', 
          height: '100%',
          position: 'relative'
        }}
        className={`custom-date-cell ${hasEvents ? 'has-events' : ''} ${hasImages ? 'has-images' : ''}`}
      >
        {children}
        {/* Ê∑ªÂä†ÂõæÁâáÊ†áËÆ∞ */}
        {hasImages && (
          <div className="image-indicator" title="Êúâ‰øùÂ≠òÁöÑÂõæÁâá">
            üñºÔ∏è
          </div>
        )}
      </div>
    );
  };

  // Êõ¥Êñ∞ÔºöËá™ÂÆö‰πâÂ∑•ÂÖ∑Ê†è - Ê∑ªÂä†ÂõæÁâáÊêúÁ¥¢ÊåâÈíÆ
  const CustomToolbar = () => {
    const currentMoment = moment(currentDate);

    return (
      <div className="custom-toolbar">
        <div className="toolbar-left">
          <button className="nav-btn prev-btn" onClick={() => navigateDate(-1)}>‚Äπ</button>

          <div className="date-display">
            <span className="current-year">{currentMoment.format('YYYY')}Âπ¥</span>
            <span className="current-month">{currentMoment.format('MM')}Êúà</span>
            {view === 'day' && <span className="current-day">{currentMoment.format('DD')}Êó•</span>}
          </div>

          <button className="nav-btn next-btn" onClick={() => navigateDate(1)}>‚Ä∫</button>
          <button className="today-btn" onClick={goToToday}>‰ªäÂ§©</button>
        </div>

        <div className="toolbar-right">
          <div className="view-controls">
            <button className={`view-btn ${view === 'month' ? 'active' : ''}`} onClick={() => handleViewChange('month')}>Êúà</button>
            <button className={`view-btn ${view === 'week' ? 'active' : ''}`} onClick={() => handleViewChange('week')}>Âë®</button>
            <button className={`view-btn ${view === 'day' ? 'active' : ''}`} onClick={() => handleViewChange('day')}>Êó•</button>
          </div>

          {/* Êñ∞Â¢ûÂõæÁâáÊêúÁ¥¢ÊåâÈíÆ */}
          <button 
            className="image-search-btn"
            onClick={() => handleImageSearch(moment().format('YYYY-MM-DD'))}
            style={{
              background: 'linear-gradient(135deg, #9b59b6, #8e44ad)',
              color: 'white',
              border: 'none',
              padding: '10px 20px',
              borderRadius: '8px',
              cursor: 'pointer',
              fontWeight: '500',
              transition: 'all 0.3s'
            }}
          >
            üñºÔ∏è ÊêúÂõæ
          </button>

          <button className="add-event-btn" onClick={handleAddEvent}>
            + Ê∑ªÂä†‰∫ã‰ª∂
          </button>
        </div>
      </div>
    );
  };

  // Ëé∑Âèñ‰ªäÊó•‰∫ã‰ª∂
  const todayEvents = storageService.getEventsByDate(new Date());
  // Ëé∑ÂèñÊú¨Êúà‰∫ã‰ª∂ÁªüËÆ°
  const monthEvents = storageService.getEventsByMonth(currentDate);

  return (
    <div className="calendar-page">
      <div className="calendar-container">
        <div className="calendar-header">
          <div className="header-content">
            <h1 className="app-title">Êó∂Â∞öÊó•ÂéÜ</h1>
            <div className="user-info">
              <div className="user-avatar">Áî®</div>
              <span className="user-name">Áî®Êà∑</span>
            </div>
          </div>
        </div>

        <div className="calendar-content">
          <div className="main-calendar">
            <CustomToolbar />

            <div className="calendar-wrapper">
              <Calendar
                localizer={localizer}
                events={events}
                startAccessor="start"
                endAccessor="end"
                view={view}
                date={currentDate}
                onView={setView}
                onNavigate={setCurrentDate}
                onSelectEvent={handleSelectEvent}
                onSelectSlot={handleSelectSlot}
                selectable
                eventPropGetter={eventStyleGetter}
                components={{
                  dayWrapper: CustomDayWrapper
                }}
                messages={{
                  next: "‰∏ã‰∏Ä‰∏™",
                  previous: "‰∏ä‰∏Ä‰∏™",
                  today: "‰ªäÂ§©",
                  month: "Êúà",
                  week: "Âë®",
                  day: "Êó•",
                  agenda: "ËÆÆÁ®ã"
                }}
                style={{ height: 500 }}
                dayPropGetter={(date) => {
                  const hasEvents = storageService.getEventsByDate(date).length > 0;
                  return {
                    className: hasEvents ? 'has-events' : ''
                  };
                }}
                popup
                step={60}
                showMultiDayTimes
                formats={{
                  dateFormat: 'D',
                  dayFormat: 'D',
                  weekdayFormat: 'ddd',
                  timeGutterFormat: 'HH:mm',
                  monthHeaderFormat: 'YYYYÂπ¥ MMÊúà',
                  dayHeaderFormat: 'YYYYÂπ¥ MMÊúà DDÊó•',
                  dayRangeHeaderFormat: ({ start, end }) => 
                    `${moment(start).format('YYYYÂπ¥ MMÊúà DDÊó•')} - ${moment(end).format('MMÊúà DDÊó•')}`
                }}
              />
            </div>
          </div>

          <div className="calendar-sidebar">
            <div className="sidebar-section">
              <h3 className="sidebar-title">
                ‰ªäÊó•‰∫ã‰ª∂
                <span className="event-count">{todayEvents.length}</span>
              </h3>

              <div className="event-list">
                {todayEvents.map(event => (
                  <div key={event.id} className={`event-item ${event.type}`}>
                    <div className="event-header">
                      <span className="event-title">{event.title}</span>
                      <span className="event-category">
                        {event.type === 'work' && 'Â∑•‰Ωú'}
                        {event.type === 'meeting' && '‰ºöËÆÆ'}
                        {event.type === 'personal' && '‰∏™‰∫∫'}
                        {event.type === 'urgent' && 'Á¥ßÊÄ•'}
                      </span>
                    </div>
                    <div className="event-time">
                      {moment(event.start).format('HH:mm')} - {moment(event.end).format('HH:mm')}
                    </div>
                  </div>
                ))}

                {todayEvents.length === 0 && (
                  <div className="empty-events">
                    <div className="empty-icon">üìÖ</div>
                    <p>‰ªäÂ§©Ê≤°ÊúâÂÆâÊéí‰∫ã‰ª∂</p>
                  </div>
                )}
              </div>
            </div>

            <div className="sidebar-section">
              <h3 className="sidebar-title">Êú¨ÊúàÁªüËÆ°</h3>
              <div className="stats-grid">
                <div className="stat-item">
                  <div className="stat-value">{monthEvents.length}</div>
                  <div className="stat-label">ÊÄª‰∫ã‰ª∂</div>
                </div>
                <div className="stat-item">
                  <div className="stat-value">
                    {monthEvents.filter(e => e.type === 'work').length}
                  </div>
                  <div className="stat-label">Â∑•‰Ωú</div>
                </div>
                <div className="stat-item">
                  <div className="stat-value">
                    {monthEvents.filter(e => e.type === 'personal').length}
                  </div>
                  <div className="stat-label">‰∏™‰∫∫</div>
                </div>
                <div className="stat-item">
                  <div className="stat-value">
                    {monthEvents.filter(e => e.type === 'urgent').length}
                  </div>
                  <div className="stat-label">Á¥ßÊÄ•</div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Ê∑ªÂä†ÂõæÁâáÊêúÁ¥¢Ê®°ÊÄÅÊ°Ü */}
      {showImageSearch && (
        <ImageSearchModal
          date={selectedDate}
          onClose={() => setShowImageSearch(false)}
          onImageSelect={handleImageSelect}
          onImageSave={handleImageSave}
        />
      )}

      {/* Ê∑ªÂä†ÊÇ¨ÂÅúÈ¢ÑËßàÂç°Áâá */}
      {renderHoverPreview()}
    </div>
  );
};

export default CalendarPage;
