import React, { useState, useEffect } from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import { useNavigate } from 'react-router-dom';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import './CalendarPage.css';

// ËÆæÁΩÆ‰∏≠ÊñáÊú¨Âú∞Âåñ
moment.locale('zh-cn');
const localizer = momentLocalizer(moment);

const CalendarPage = () => {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [view, setView] = useState('month');
  const [hoveredDate, setHoveredDate] = useState(null);
  const [previewEvents, setPreviewEvents] = useState([]);
  const navigate = useNavigate();

  // ‰ªé localStorage Âä†ËΩΩ‰øùÂ≠òÁöÑ‰∫ã‰ª∂
  const [events, setEvents] = useState(() => {
    const savedEvents = localStorage.getItem('calendar-events');
    if (savedEvents) {
      const parsedEvents = JSON.parse(savedEvents);
      return parsedEvents.map(event => ({
        ...event,
        start: new Date(event.start),
        end: new Date(event.end)
      }));
    } else {
      const currentYear = new Date().getFullYear();
      const currentMonth = new Date().getMonth();
      const currentDay = new Date().getDate();

      return [
        {
          id: 1,
          title: 'Âõ¢ÈòüÂë®‰ºö',
          start: new Date(currentYear, currentMonth, currentDay, 10, 0),
          end: new Date(currentYear, currentMonth, currentDay, 11, 30),
          type: 'work'
        },
        {
          id: 2,
          title: 'È°πÁõÆËØÑÂÆ°',
          start: new Date(currentYear, currentMonth, currentDay + 1, 14, 0),
          end: new Date(currentYear, currentMonth, currentDay + 1, 16, 0),
          type: 'meeting'
        },
        {
          id: 3,
          title: 'ÂÅ•Ë∫´ËÆ≠ÁªÉ',
          start: new Date(currentYear, currentMonth, currentDay, 18, 0),
          end: new Date(currentYear, currentMonth, currentDay, 19, 0),
          type: 'personal'
        },
        {
          id: 4,
          title: 'ÂÆ¢Êà∑‰ºöËÆÆ',
          start: new Date(currentYear, currentMonth, currentDay - 1, 9, 0),
          end: new Date(currentYear, currentMonth, currentDay - 1, 10, 30),
          type: 'urgent'
        }
      ];
    }
  });

  // ÂΩì‰∫ã‰ª∂ÂèòÂåñÊó∂‰øùÂ≠òÂà∞ localStorage
  useEffect(() => {
    localStorage.setItem('calendar-events', JSON.stringify(events));
  }, [events]);

  // Â§ÑÁêÜÈº†Ê†áÊÇ¨ÂÅú
  const handleDayMouseEnter = (date) => {
    setHoveredDate(date);
    const dayEvents = events.filter(event => 
      moment(event.start).isSame(date, 'day')
    );
    setPreviewEvents(dayEvents);
  };

  const handleDayMouseLeave = () => {
    setHoveredDate(null);
    setPreviewEvents([]);
  };

  // Â§ÑÁêÜÊó•ÊúüÁÇπÂáªË∑≥ËΩ¨
  const handleDateClick = (date) => {
    const dateString = moment(date).format('YYYY-MM-DD');
    navigate(`/date/${dateString}`);
  };

  // ‰øÆÂ§çÔºö‰ΩøÁî®Ê≠£Á°ÆÁöÑÁªÑ‰ª∂Ë¶ÜÁõñÊñπÂºè
 // ‰øÆÂ§çÂêéÁöÑ CustomMonthDateCell ÁªÑ‰ª∂
const CustomMonthDateCell = ({ date }) => {
  const dayEvents = events.filter(event => 
    moment(event.start).isSame(date, 'day')
  );

  return (
    <div 
      className="custom-date-cell"
      onClick={(e) => {
        e.stopPropagation();
        handleDateClick(date);
      }}
    >
      <div className="date-number">
        {moment(date).date()}
      </div>
      {dayEvents.length > 0 && (
        <div className="event-dots">
          {dayEvents.slice(0, 3).map((event, index) => (
            <div 
              key={index}
              className={`event-dot ${event.type}`}
              title={event.title}
            />
          ))}
        </div>
      )}
    </div>
  );
};

// Âú® Calendar ÁªÑ‰ª∂‰∏≠‰ΩøÁî®Ê≠£Á°ÆÁöÑÈÖçÁΩÆ
<Calendar
  localizer={localizer}
  events={events}
  startAccessor="start"
  endAccessor="end"
  view={view}
  date={currentDate}
  onView={setView}
  onNavigate={setCurrentDate}
  onSelectEvent={handleSelectEvent}
  onSelectSlot={handleSelectSlot}
  selectable
  eventPropGetter={eventStyleGetter}
  components={{
    dateCellWrapper: (props) => {
      const { value } = props;
      return (
        <div 
          className="date-cell-wrapper"
          onMouseEnter={() => handleDayMouseEnter(value)}
          onMouseLeave={handleDayMouseLeave}
          onClick={() => handleDateClick(value)}
          style={{ 
            height: '100%', 
            cursor: 'pointer',
            position: 'relative'
          }}
        >
          {props.children}
        </div>
      );
    },
    month: {
      dateHeader: ({ date, label }) => (
        <CustomMonthDateCell date={date} />
      )
    }
  }}
  messages={{
    next: "‰∏ã‰∏Ä‰∏™",
    previous: "‰∏ä‰∏Ä‰∏™",
    today: "‰ªäÂ§©",
    month: "Êúà",
    week: "Âë®",
    day: "Êó•",
    agenda: "ËÆÆÁ®ã"
  }}
  style={{ height: 500 }}
  popup
  step={60}
  showMultiDayTimes
/>

  // ‰øÆÂ§çÔºöÊ∑ªÂä†Êó•ÊúüÂçïÂÖÉÊ†ºÂåÖË£ÖÂô®
  const CustomDateCellWrapper = ({ children, value }) => {
    return (
      <div 
        className="date-cell-wrapper"
        onMouseEnter={() => handleDayMouseEnter(value)}
        onMouseLeave={handleDayMouseLeave}
        onClick={() => handleDateClick(value)}
        style={{ 
          height: '100%', 
          cursor: 'pointer',
          position: 'relative'
        }}
      >
        {children}
      </div>
    );
  };

  // Êó•ÊúüÂØºËà™
  const navigateDate = (direction) => {
    let newDate;
    if (view === 'month') {
      newDate = moment(currentDate).add(direction, 'months').toDate();
    } else if (view === 'week') {
      newDate = moment(currentDate).add(direction, 'weeks').toDate();
    } else {
      newDate = moment(currentDate).add(direction, 'days').toDate();
    }
    setCurrentDate(newDate);
  };

  const goToToday = () => {
    setCurrentDate(new Date());
  };

  const handleViewChange = (newView) => {
    setView(newView);
  };

  const eventStyleGetter = (event) => {
    let backgroundColor = '#3174ad';

    switch (event.type) {
      case 'work':
        backgroundColor = '#3498db';
        break;
      case 'meeting':
        backgroundColor = '#9b59b6';
        break;
      case 'personal':
        backgroundColor = '#2ecc71';
        break;
      case 'urgent':
        backgroundColor = '#e74c3c';
        break;
      default:
        backgroundColor = '#3174ad';
    }

    return {
      style: {
        backgroundColor,
        borderRadius: '4px',
        opacity: 0.9,
        color: 'white',
        border: 'none',
        fontSize: '12px',
        fontWeight: '500',
        padding: '2px 4px'
      }
    };
  };

  const handleSelectEvent = (event) => {
    alert(`‰∫ã‰ª∂: ${event.title}\nÂºÄÂßã: ${moment(event.start).format('YYYY-MM-DD HH:mm')}\nÁªìÊùü: ${moment(event.end).format('YYYY-MM-DD HH:mm')}`);
  };

  const handleSelectSlot = ({ start, end }) => {
    const title = window.prompt('ËØ∑ËæìÂÖ•Êñ∞‰∫ã‰ª∂ÂêçÁß∞:');
    if (title) {
      const startDate = new Date(start);
      const endDate = new Date(end);

      if (startDate.getTime() === endDate.getTime()) {
        endDate.setHours(startDate.getHours() + 1);
      }

      const newEvent = {
        id: Date.now(),
        title,
        start: startDate,
        end: endDate,
        type: 'work'
      };

      setEvents(prev => [...prev, newEvent]);
    }
  };

  // Ê∑ªÂä†‰∫ã‰ª∂Á±ªÂûãÈÄâÊã©ÂäüËÉΩ
  const handleAddEventWithType = () => {
    const title = window.prompt('ËØ∑ËæìÂÖ•Êñ∞‰∫ã‰ª∂ÂêçÁß∞:');
    if (title) {
      const type = window.prompt('ËØ∑ËæìÂÖ•‰∫ã‰ª∂Á±ªÂûã (work/meeting/personal/urgent):', 'work');
      const now = new Date();
      const end = new Date(now.getTime() + 60 * 60 * 1000);

      const newEvent = {
        id: Date.now(),
        title,
        start: now,
        end: end,
        type: type || 'work'
      };

      setEvents(prev => [...prev, newEvent]);
    }
  };

  const CustomToolbar = () => {
    const currentMoment = moment(currentDate);

    return (
      <div className="custom-toolbar">
        <div className="toolbar-left">
          <button className="nav-btn prev-btn" onClick={() => navigateDate(-1)}>‚Äπ</button>

          <div className="date-display">
            <span className="current-year">{currentMoment.format('YYYY')}Âπ¥</span>
            <span className="current-month">{currentMoment.format('MM')}Êúà</span>
            {view === 'day' && <span className="current-day">{currentMoment.format('DD')}Êó•</span>}
          </div>

          <button className="nav-btn next-btn" onClick={() => navigateDate(1)}>‚Ä∫</button>
          <button className="today-btn" onClick={goToToday}>‰ªäÂ§©</button>
        </div>

        <div className="toolbar-right">
          <div className="view-controls">
            <button className={`view-btn ${view === 'month' ? 'active' : ''}`} onClick={() => handleViewChange('month')}>Êúà</button>
            <button className={`view-btn ${view === 'week' ? 'active' : ''}`} onClick={() => handleViewChange('week')}>Âë®</button>
            <button className={`view-btn ${view === 'day' ? 'active' : ''}`} onClick={() => handleViewChange('day')}>Êó•</button>
          </div>

          <button className="add-event-btn" onClick={handleAddEventWithType}>
            + Ê∑ªÂä†‰∫ã‰ª∂
          </button>
        </div>
      </div>
    );
  };

  return (
    <div className="calendar-page">
      <div className="calendar-container">
        <div className="calendar-header">
          <div className="header-content">
            <h1 className="app-title">Êô∫ËÉΩÊó•Á®ãÊó•ÂéÜ</h1>
            <div className="user-info">
              <div className="user-avatar">Áî®</div>
              <span className="user-name">Áî®Êà∑</span>
            </div>
          </div>
        </div>

        <div className="calendar-content">
          <div className="main-calendar">
            <CustomToolbar />

            {/* ÊÇ¨ÂÅúÈ¢ÑËßàÂç°Áâá */}
            {hoveredDate && previewEvents.length > 0 && (
              <div className="date-preview-card">
                <div className="preview-header">
                  {moment(hoveredDate).format('MMÊúàDDÊó•')} ÁöÑ‰∫ã‰ª∂
                </div>
                <div className="preview-events">
                  {previewEvents.map(event => (
                    <div key={event.id} className={`preview-event ${event.type}`}>
                      <span className="preview-event-title">{event.title}</span>
                      <span className="preview-event-time">
                        {moment(event.start).format('HH:mm')}
                      </span>
                    </div>
                  ))}
                </div>
              </div>
            )}

            <div className="calendar-wrapper">
              <Calendar
                localizer={localizer}
                events={events}
                startAccessor="start"
                endAccessor="end"
                view={view}
                date={currentDate}
                onView={setView}
                onNavigate={setCurrentDate}
                onSelectEvent={handleSelectEvent}
                onSelectSlot={handleSelectSlot}
                selectable
                eventPropGetter={eventStyleGetter}
              // Âú® CalendarPage.jsx ‰∏≠Ôºå‰øÆÊîπ components ÈÖçÁΩÆÔºö

components={{
  // ‰øÆÂ§çÊó•ÊúüÂçïÂÖÉÊ†ºÂåÖË£ÖÂô®
  dateCellWrapper: (props) => {
    const { value } = props;
    return (
      <div 
        className="date-cell-wrapper"
        onMouseEnter={() => handleDayMouseEnter(value)}
        onMouseLeave={handleDayMouseLeave}
        onClick={() => handleDateClick(value)}
        style={{ 
          height: '100%', 
          cursor: 'pointer',
          position: 'relative'
        }}
      >
        {props.children}
      </div>
    );
  },
  // ‰øÆÂ§çÊúà‰ªΩËßÜÂõæÁöÑÊó•ÊúüÂ§¥ÈÉ®
  month: {
    dateHeader: ({ date, label }) => (
      <CustomMonthDateCell date={date} />
    )
  }
}}
                messages={{
                  next: "‰∏ã‰∏Ä‰∏™",
                  previous: "‰∏ä‰∏Ä‰∏™",
                  today: "‰ªäÂ§©",
                  month: "Êúà",
                  week: "Âë®",
                  day: "Êó•",
                  agenda: "ËÆÆÁ®ã"
                }}
                style={{ height: 500 }}
                popup
                step={60}
                showMultiDayTimes
              />
            </div>
          </div>

          <div className="calendar-sidebar">
            <div className="sidebar-section">
              <h3 className="sidebar-title">
                ‰ªäÊó•‰∫ã‰ª∂
                <span className="event-count">
                  {events.filter(event => moment(event.start).isSame(moment(), 'day')).length}
                </span>
              </h3>

              <div className="event-list">
                {events
                  .filter(event => moment(event.start).isSame(moment(), 'day'))
                  .map(event => (
                    <div key={event.id} className={`event-item ${event.type}`}>
                      <div className="event-header">
                        <span className="event-title">{event.title}</span>
                        <span className="event-category">
                          {event.type === 'work' && 'Â∑•‰Ωú'}
                          {event.type === 'meeting' && '‰ºöËÆÆ'}
                          {event.type === 'personal' && '‰∏™‰∫∫'}
                          {event.type === 'urgent' && 'Á¥ßÊÄ•'}
                        </span>
                      </div>
                      <div className="event-time">
                        {moment(event.start).format('HH:mm')} - {moment(event.end).format('HH:mm')}
                      </div>
                    </div>
                  ))
                }

                {events.filter(event => moment(event.start).isSame(moment(), 'day')).length === 0 && (
                  <div className="empty-events">
                    <div className="empty-icon">üìÖ</div>
                    <p>‰ªäÂ§©Ê≤°ÊúâÂÆâÊéí‰∫ã‰ª∂</p>
                  </div>
                )}
              </div>
            </div>

            <div className="sidebar-section">
              <h3 className="sidebar-title">Êú¨ÊúàÁªüËÆ°</h3>
              <div className="stats-grid">
                <div className="stat-item">
                  <div className="stat-value">{events.length}</div>
                  <div className="stat-label">ÊÄª‰∫ã‰ª∂</div>
                </div>
                <div className="stat-item">
                  <div className="stat-value">
                    {events.filter(e => e.type === 'work').length}
                  </div>
                  <div className="stat-label">Â∑•‰Ωú</div>
                </div>
                <div className="stat-item">
                  <div className="stat-value">
                    {events.filter(e => e.type === 'personal').length}
                  </div>
                  <div className="stat-label">‰∏™‰∫∫</div>
                </div>
                <div className="stat-item">
                  <div className="stat-value">
                    {events.filter(e => e.type === 'urgent').length}
                  </div>
                  <div className="stat-label">Á¥ßÊÄ•</div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CalendarPage;
