import React, { useState } from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import './CalendarPage.css';

// 设置中文本地化
moment.locale('zh-cn');
const localizer = momentLocalizer(moment);

// 正确的 CustomDateCell 组件实现
const CustomDateCell = ({ value, children }) => {
  // 安全检查
  if (!value) {
    return (
      <div className="date-cell-wrapper">
        {children}
      </div>
    );
  }

  try {
    const dateMoment = moment(value);
    const hasEvents = events.some(event => 
      event.start && moment(event.start).isSame(dateMoment, 'day')
    );

    return (
      <div className="date-cell-wrapper">
        {children}
        {hasEvents && <span className="event-indicator"></span>}
      </div>
    );
  } catch (error) {
    console.error('CustomDateCell error:', error);
    return (
      <div className="date-cell-wrapper">
        {children}
      </div>
    );
  }
};

export default CalendarPage;
