// src/components/EventDetailPage.jsx
import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import moment from 'moment';
import './EventDetailPage.css';
import { storageService } from '../utils/storage';

const EventDetailPage = () => {
  const { date } = useParams();
  const navigate = useNavigate();
  const [content, setContent] = useState('');
  const [backgroundImage, setBackgroundImage] = useState('');
  const [events, setEvents] = useState([]);
  const [showImageUpload, setShowImageUpload] = useState(false);
  const editorRef = useRef(null);

  // Âä†ËΩΩËØ•Êó•ÊúüÁöÑ‰∫ã‰ª∂Êï∞ÊçÆ
  useEffect(() => {
    const savedContent = storageService.getDailyContent(date);
    const savedBackground = storageService.getDailyBackground(date);
    const dateEvents = storageService.getEventsByDate(date);

    setContent(savedContent || '');
    setBackgroundImage(savedBackground || '');
    setEvents(dateEvents);
  }, [date]);

  // ÂàùÂßãÂåñÁºñËæëÂô®ÂÜÖÂÆπ
  useEffect(() => {
    if (editorRef.current && content !== undefined) {
      editorRef.current.innerHTML = content || '';
    }
  }, [content]);

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        const imageUrl = reader.result;

        // ‰ΩøÁî®Êõ¥ÂÆâÂÖ®ÁöÑÊñπÂºèÊèíÂÖ•ÂõæÁâá
        if (editorRef.current) {
          const selection = window.getSelection();
          const range = selection.rangeCount > 0 ? selection.getRangeAt(0) : null;

          const img = document.createElement('img');
          img.src = imageUrl;
          img.alt = "uploaded";
          img.style.maxWidth = "100%";
          img.style.borderRadius = "8px";
          img.style.margin = "10px 0";

          if (range) {
            range.insertNode(img);
            range.collapse(false);
          } else {
            editorRef.current.appendChild(img);
          }

          // Ëß¶ÂèëÂÜÖÂÆπÊõ¥Êñ∞
          const newContent = editorRef.current.innerHTML;
          setContent(newContent);
          storageService.saveDailyContent(date, newContent);
        }
      };
      reader.readAsDataURL(file);
    }
    // Ê∏ÖÁ©∫inputÔºåÂÖÅËÆ∏ÈáçÂ§çÈÄâÊã©Âêå‰∏ÄÊñá‰ª∂
    e.target.value = '';
  };

  const handleBackgroundUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        const imageUrl = reader.result;
        setBackgroundImage(imageUrl);
        storageService.saveDailyBackground(date, imageUrl);
      };
      reader.readAsDataURL(file);
    }
    // Ê∏ÖÁ©∫inputÔºåÂÖÅËÆ∏ÈáçÂ§çÈÄâÊã©Âêå‰∏ÄÊñá‰ª∂
    e.target.value = '';
  };

  const saveContent = () => {
    storageService.saveDailyContent(date, content);
    alert('ÂÜÖÂÆπÂ∑≤‰øùÂ≠ò');
  };

  const handleContentChange = (e) => {
    const newContent = e.target.innerHTML;
    setContent(newContent);
    // Ëá™Âä®‰øùÂ≠ò
    storageService.saveDailyContent(date, newContent);
  };

  const addNewEvent = () => {
    const title = prompt('ËØ∑ËæìÂÖ•‰∫ã‰ª∂Ê†áÈ¢ò:');
    if (!title) return;

    const type = prompt('ËØ∑ËæìÂÖ•‰∫ã‰ª∂Á±ªÂûã (work/meeting/personal/urgent):', 'work');
    const startTime = prompt('ËØ∑ËæìÂÖ•ÂºÄÂßãÊó∂Èó¥ (HH:mm)', '09:00');
    const endTime = prompt('ËØ∑ËæìÂÖ•ÁªìÊùüÊó∂Èó¥ (HH:mm)', '10:00');

    // È™åËØÅÊó∂Èó¥Ê†ºÂºè
    if (!startTime || !endTime || !startTime.match(/^\d{2}:\d{2}$/) || !endTime.match(/^\d{2}:\d{2}$/)) {
      alert('Êó∂Èó¥Ê†ºÂºè‰∏çÊ≠£Á°ÆÔºåËØ∑‰ΩøÁî® HH:mm Ê†ºÂºè');
      return;
    }

    // È™åËØÅÊó∂Èó¥ÈÄªËæë
    const start = new Date(date + 'T' + startTime + ':00');
    const end = new Date(date + 'T' + endTime + ':00');

    if (end <= start) {
      alert('ÁªìÊùüÊó∂Èó¥ÂøÖÈ°ªÊôö‰∫éÂºÄÂßãÊó∂Èó¥');
      return;
    }

    const newEvent = {
      title,
      start: start,
      end: end,
      type: type || 'work'
    };

    const savedEvent = storageService.addEvent(newEvent);
    setEvents(prev => [...prev, savedEvent]);
  };

  const deleteEvent = (eventId) => {
    if (window.confirm('Á°ÆÂÆöË¶ÅÂà†Èô§Ëøô‰∏™‰∫ã‰ª∂ÂêóÔºü')) {
      const updatedEvents = storageService.deleteEvent(eventId);
      setEvents(updatedEvents.filter(event => 
        moment(event.start).isSame(date, 'day')
      ));
    }
  };

  const formatEventType = (type) => {
    const typeMap = {
      'work': 'Â∑•‰Ωú',
      'meeting': '‰ºöËÆÆ',
      'personal': '‰∏™‰∫∫',
      'urgent': 'Á¥ßÊÄ•'
    };
    return typeMap[type] || type;
  };

  const getEventTypeColor = (type) => {
    const colorMap = {
      'work': '#3498db',
      'meeting': '#9b59b6',
      'personal': '#2ecc71',
      'urgent': '#e74c3c'
    };
    return colorMap[type] || '#3498db';
  };

  return (
    <div className="event-detail-page">
      <div className="detail-header">
        <button className="back-btn" onClick={() => navigate('/')}>
          ‚Üê ËøîÂõûÊó•ÂéÜ
        </button>
        <h1 className="detail-title">{moment(date).format('YYYYÂπ¥MMÊúàDDÊó•')}</h1>
        <button className="save-btn" onClick={saveContent}>
          üíæ ‰øùÂ≠òÂÜÖÂÆπ
        </button>
      </div>

      <div className="detail-content">
        <div className="editor-toolbar">
          <button 
            className="toolbar-btn" 
            onClick={() => setShowImageUpload(!showImageUpload)}
          >
            üì∑ Ê∑ªÂä†ÂõæÁâá
          </button>

          <label className="toolbar-btn">
            üñºÔ∏è ËÉåÊôØÂõæÁâá
            <input 
              type="file" 
              accept="image/*" 
              onChange={handleBackgroundUpload}
              style={{ display: 'none' }}
            />
          </label>

          <button className="toolbar-btn" onClick={addNewEvent}>
            ‚ûï Ê∑ªÂä†‰∫ã‰ª∂
          </button>

          {showImageUpload && (
            <div className="image-upload-panel">
              <span>ÈÄâÊã©ÂõæÁâáÔºö</span>
              <input 
                type="file" 
                accept="image/*" 
                onChange={handleImageUpload}
                multiple
              />
            </div>
          )}
        </div>

        <div className="events-section">
          <h3>üìÖ ‰ªäÊó•‰∫ã‰ª∂ ({events.length})</h3>
          {events.length > 0 ? (
            <div className="events-list">
              {events.map(event => (
                <div 
                  key={event.id} 
                  className="event-card"
                  style={{ borderLeftColor: getEventTypeColor(event.type) }}
                >
                  <div>
                    <h4>{event.title}</h4>
                    <p>
                      {moment(event.start).format('HH:mm')} - {moment(event.end).format('HH:mm')}
                    </p>
                  </div>
                  <div className="event-actions">
                    <span 
                      className="event-type"
                      style={{ backgroundColor: getEventTypeColor(event.type) }}
                    >
                      {formatEventType(event.type)}
                    </span>
                    <button 
                      className="delete-event-btn"
                      onClick={() => deleteEvent(event.id)}
                    >
                      Âà†Èô§
                    </button>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <p className="no-events">ÊöÇÊó†‰∫ã‰ª∂ÔºåÁÇπÂáª"Ê∑ªÂä†‰∫ã‰ª∂"ÊåâÈíÆÂàõÂª∫Êñ∞‰∫ã‰ª∂</p>
          )}
        </div>

        <div 
          ref={editorRef}
          className="content-editor"
          contentEditable
          suppressContentEditableWarning
          onInput={handleContentChange}
          style={{ 
            backgroundImage: backgroundImage ? `url(${backgroundImage})` : 'none',
            backgroundSize: 'cover',
            backgroundPosition: 'center',
            backgroundBlendMode: backgroundImage ? 'overlay' : 'normal',
            backgroundColor: backgroundImage ? 'rgba(255, 255, 255, 0.8)' : 'transparent'
          }}
        />
      </div>
    </div>
  );
};

export default EventDetailPage;
