  const currentMonth = new Date().getMonth();   // 9 (10Êúà)
  const currentDay = new Date().getDate();      // ÂΩìÂâçÊó•Êúü

  const [events, setEvents] = useState([
    {
      id: 1,
      title: 'Âõ¢ÈòüÂë®‰ºö',
      start: new Date(currentYear, currentMonth, currentDay, 10, 0),     // ‰ªäÂ§©10:00
      end: new Date(currentYear, currentMonth, currentDay, 11, 30),      // ‰ªäÂ§©11:30
      type: 'work'
    },
    {
      id: 2,
      title: 'È°πÁõÆËØÑÂÆ°',
      start: new Date(currentYear, currentMonth, currentDay + 1, 14, 0), // ÊòéÂ§©14:00
      end: new Date(currentYear, currentMonth, currentDay + 1, 16, 0),   // ÊòéÂ§©16:00
      type: 'meeting'
    },
    {
      id: 3,
      title: 'ÂÅ•Ë∫´ËÆ≠ÁªÉ',
      start: new Date(currentYear, currentMonth, currentDay, 18, 0),     // ‰ªäÂ§©18:00
      end: new Date(currentYear, currentMonth, currentDay, 19, 0),       // ‰ªäÂ§©19:00
      type: 'personal'
    },
    {
      id: 4,
