// src/components/EventDetailPage.jsx
import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import moment from 'moment';
import './EventDetailPage.css';
import { storageService } from '../utils/storage';

const EventDetailPage = () => {
  const { date } = useParams();
  const navigate = useNavigate();
  const [content, setContent] = useState('');
  const [backgroundImage, setBackgroundImage] = useState('');
  const [events, setEvents] = useState([]);
  const [showImageUpload, setShowImageUpload] = useState(false);
  const editorRef = useRef(null);

  // Âä†ËΩΩËØ•Êó•ÊúüÁöÑ‰∫ã‰ª∂Êï∞ÊçÆ
  useEffect(() => {
    const savedContent = storageService.getDailyContent(date);
    const savedBackground = storageService.getDailyBackground(date);
    const dateEvents = storageService.getEventsByDate(date);

    setContent(savedContent || '');
    setBackgroundImage(savedBackground || '');
    setEvents(dateEvents);
  }, [date]);

  // ÂΩìÂÜÖÂÆπÂä†ËΩΩÂêéÔºåËÆæÁΩÆÁºñËæëÂô®ÂÜÖÂÆπ
  useEffect(() => {
    if (editorRef.current) {
      editorRef.current.innerHTML = content || '';
      // Â¶ÇÊûúÊ≤°ÊúâÂÜÖÂÆπÔºåÊ∑ªÂä†Âç†‰ΩçÁ¨¶
      if (!content) {
        editorRef.current.classList.add('empty');
      } else {
        editorRef.current.classList.remove('empty');
      }
    }
  }, [content]);

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        const imageUrl = reader.result;
        const imgHTML = `<img src="${imageUrl}" alt="uploaded" style="max-width: 100%; border-radius: 8px; margin: 10px 0;" />`;

        // ÊèíÂÖ•ÂõæÁâáÂà∞ÂΩìÂâçÂÖâÊ†á‰ΩçÁΩÆ
        if (editorRef.current) {
          const selection = window.getSelection();
          if (selection.rangeCount > 0) {
            const range = selection.getRangeAt(0);
            const imgNode = document.createElement('div');
            imgNode.innerHTML = imgHTML;
            range.insertNode(imgNode);

            // Êõ¥Êñ∞ÂÜÖÂÆπÁä∂ÊÄÅ
            const newContent = editorRef.current.innerHTML;
            setContent(newContent);
            storageService.saveDailyContent(date, newContent);
          }
        }
      };
      reader.readAsDataURL(file);
    }
  };

  const handleBackgroundUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        const imageUrl = reader.result;
        setBackgroundImage(imageUrl);
        storageService.saveDailyBackground(date, imageUrl);
      };
      reader.readAsDataURL(file);
    }
  };

  const saveContent = () => {
    if (editorRef.current) {
      const currentContent = editorRef.current.innerHTML;
      storageService.saveDailyContent(date, currentContent);
      alert('ÂÜÖÂÆπÂ∑≤‰øùÂ≠ò');
    }
  };

  const handleContentChange = () => {
    if (editorRef.current) {
      const newContent = editorRef.current.innerHTML;
      setContent(newContent);

      // Ê†πÊçÆÂÜÖÂÆπÊòæÁ§∫/ÈöêËóèÂç†‰ΩçÁ¨¶
      if (newContent === '<br>' || newContent === '') {
        editorRef.current.classList.add('empty');
      } else {
        editorRef.current.classList.remove('empty');
      }

      // Ëá™Âä®‰øùÂ≠ò
      storageService.saveDailyContent(date, newContent);
    }
  };

  const handleEditorFocus = () => {
    if (editorRef.current) {
      editorRef.current.classList.remove('empty');
      // Â¶ÇÊûúÂÜÖÂÆπÊòØÁ©∫ÁöÑÔºåÊ∏ÖÁ©∫‰ª•‰æøËæìÂÖ•
      if (editorRef.current.innerHTML === 'Âú®ËøôÈáåËæìÂÖ•ÂÜÖÂÆπ...') {
        editorRef.current.innerHTML = '';
      }
    }
  };

  const handleEditorBlur = () => {
    if (editorRef.current) {
      if (!editorRef.current.innerHTML || editorRef.current.innerHTML === '<br>') {
        editorRef.current.classList.add('empty');
        editorRef.current.innerHTML = 'Âú®ËøôÈáåËæìÂÖ•ÂÜÖÂÆπ...';
      }
    }
  };

  const addNewEvent = () => {
    const title = prompt('ËØ∑ËæìÂÖ•‰∫ã‰ª∂Ê†áÈ¢ò:');
    if (!title) return;

    const type = prompt('ËØ∑ËæìÂÖ•‰∫ã‰ª∂Á±ªÂûã (work/meeting/personal/urgent):', 'work');
    const startTime = prompt('ËØ∑ËæìÂÖ•ÂºÄÂßãÊó∂Èó¥ (HH:mm)', '09:00');
    const endTime = prompt('ËØ∑ËæìÂÖ•ÁªìÊùüÊó∂Èó¥ (HH:mm)', '10:00');

    const [startHour, startMinute] = startTime.split(':');
    const [endHour, endMinute] = endTime.split(':');

    const newEvent = {
      title,
      start: new Date(date + 'T' + startTime + ':00'),
      end: new Date(date + 'T' + endTime + ':00'),
      type: type || 'work'
    };

    const savedEvent = storageService.addEvent(newEvent);
    setEvents(prev => [...prev, savedEvent]);
  };

  const deleteEvent = (eventId) => {
    if (window.confirm('Á°ÆÂÆöË¶ÅÂà†Èô§Ëøô‰∏™‰∫ã‰ª∂ÂêóÔºü')) {
      const updatedEvents = storageService.deleteEvent(eventId);
      setEvents(updatedEvents.filter(event => 
        moment(event.start).isSame(date, 'day')
      ));
    }
  };

  return (
    <div className="event-detail-page">
      <div className="detail-header">
        <button className="back-btn" onClick={() => navigate('/')}>
          ‚Üê ËøîÂõûÊó•ÂéÜ
        </button>
        <h1 className="detail-title">{moment(date).format('YYYYÂπ¥MMÊúàDDÊó•')}</h1>
        <button className="save-btn" onClick={saveContent}>
          üíæ ‰øùÂ≠ò
        </button>
      </div>

      <div className="detail-content">
        <div className="editor-toolbar">
          <button className="toolbar-btn" onClick={() => setShowImageUpload(!showImageUpload)}>
            üì∑ Ê∑ªÂä†ÂõæÁâá
          </button>
          <label className="toolbar-btn">
            üñºÔ∏è ËÉåÊôØÂõæÁâá
            <input 
              type="file" 
              accept="image/*" 
              onChange={handleBackgroundUpload}
              style={{ display: 'none' }}
            />
          </label>
          <button className="toolbar-btn" onClick={addNewEvent}>
            ‚ûï Ê∑ªÂä†‰∫ã‰ª∂
          </button>

          {showImageUpload && (
            <div className="image-upload-panel">
              <input 
                type="file" 
                accept="image/*" 
                onChange={handleImageUpload}
                multiple
              />
            </div>
          )}
        </div>

        <div className="events-section">
          <h3>‰ªäÊó•‰∫ã‰ª∂ ({events.length})</h3>
          {events.length > 0 ? (
            <div className="events-list">
              {events.map(event => (
                <div key={event.id} className="event-card">
                  <div>
                    <h4>{event.title}</h4>
                    <p>
                      {moment(event.start).format('HH:mm')} - {moment(event.end).format('HH:mm')}
                    </p>
                  </div>
                  <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
                    <span className="event-type">
                      {event.type === 'work' && 'Â∑•‰Ωú'}
                      {event.type === 'meeting' && '‰ºöËÆÆ'}
                      {event.type === 'personal' && '‰∏™‰∫∫'}
                      {event.type === 'urgent' && 'Á¥ßÊÄ•'}
                    </span>
                    <button 
                      onClick={() => deleteEvent(event.id)}
                      style={{
                        background: '#e74c3c',
                        color: 'white',
                        border: 'none',
                        borderRadius: '4px',
                        padding: '4px 8px',
                        cursor: 'pointer',
                        fontSize: '12px'
                      }}
                    >
                      Âà†Èô§
                    </button>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <p className="no-events">ÊöÇÊó†‰∫ã‰ª∂ÔºåÁÇπÂáª"Ê∑ªÂä†‰∫ã‰ª∂"ÊåâÈíÆÂàõÂª∫Êñ∞‰∫ã‰ª∂</p>
          )}
        </div>

        <div 
          ref={editorRef}
          className="content-editor empty"
          contentEditable
          suppressContentEditableWarning
          onInput={handleContentChange}
          onFocus={handleEditorFocus}
          onBlur={handleEditorBlur}
          style={{ 
            backgroundImage: backgroundImage ? `url(${backgroundImage})` : 'none',
            backgroundSize: 'cover',
            backgroundPosition: 'center'
          }}
        />
      </div>
    </div>
  );
};

export default EventDetailPage;
