// src/components/ImageSearchModal.jsx
import React, { useState, useEffect, useCallback } from 'react';
import { unsplashService } from '../services/unsplashService';
import { storageService } from '../utils/storage'; // Ê∑ªÂä†ËøôË°åÂØºÂÖ•
import './ImageSearchModal.css';

const ImageSearchModal = ({ date, onClose, onImageSelect, onImageSave, onSetAsBackground }) => {
  const [searchQuery, setSearchQuery] = useState('');
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [activeTab, setActiveTab] = useState('search'); // 'search' or 'saved'

  // ÊêúÁ¥¢ÂõæÁâá
  const searchImages = useCallback(async (query) => {
    if (!query.trim()) return;

    setLoading(true);
    setError('');
    try {
      const results = await unsplashService.searchImages(query);
      setImages(results);
    } catch (err) {
      setError('ÊêúÁ¥¢Â§±Ë¥•ÔºåËØ∑Ê£ÄÊü•ÁΩëÁªúËøûÊé•ÊàñÁ®çÂêéÈáçËØï');
      console.error('ÊêúÁ¥¢ÂõæÁâáÂ§±Ë¥•:', err);
    } finally {
      setLoading(false);
    }
  }, []);

  // Ëé∑ÂèñÈöèÊú∫ÂõæÁâá
  const loadRandomImages = useCallback(async () => {
    setLoading(true);
    setError('');
    try {
      const results = await unsplashService.getRandomImages();
      setImages(results);
    } catch (err) {
      setError('Âä†ËΩΩÂõæÁâáÂ§±Ë¥•ÔºåËØ∑Ê£ÄÊü•ÁΩëÁªúËøûÊé•');
      console.error('Âä†ËΩΩÈöèÊú∫ÂõæÁâáÂ§±Ë¥•:', err);
    } finally {
      setLoading(false);
    }
  }, []);

  // ÂàùÂßãÂä†ËΩΩÈöèÊú∫ÂõæÁâá
  useEffect(() => {
    loadRandomImages();
  }, [loadRandomImages]);

  // Â§ÑÁêÜÊêúÁ¥¢
  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      searchImages(searchQuery);
    }
  };

  // Ëé∑ÂèñÂ∑≤‰øùÂ≠òÁöÑÂõæÁâá
  const savedImages = storageService.getDailyImages(date);

  return (
    <div className="image-search-modal">
      <div className="modal-overlay" onClick={onClose}></div>
      <div className="modal-content">
        <div className="modal-header">
          <h2>ÂõæÁâáÊêúÁ¥¢‰∏éÊî∂Ëóè</h2>
          <button className="close-btn" onClick={onClose}>√ó</button>
        </div>

        <div className="modal-tabs">
          <button 
            className={`tab-btn ${activeTab === 'search' ? 'active' : ''}`}
            onClick={() => setActiveTab('search')}
          >
            üîç ÊêúÁ¥¢ÂõæÁâá
          </button>
          <button 
            className={`tab-btn ${activeTab === 'saved' ? 'active' : ''}`}
            onClick={() => setActiveTab('saved')}
          >
            üíæ Â∑≤‰øùÂ≠ò ({savedImages.length})
          </button>
        </div>

        {activeTab === 'search' && (
          <div className="search-section">
            <form onSubmit={handleSearch} className="search-form">
              <input
                type="text"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                placeholder="ËæìÂÖ•ÂÖ≥ÈîÆËØçÊêúÁ¥¢ÂõæÁâá..."
                className="search-input"
              />
              <button type="submit" className="search-btn" disabled={loading}>
                {loading ? 'ÊêúÁ¥¢‰∏≠...' : 'ÊêúÁ¥¢'}
              </button>
              <button 
                type="button" 
                className="random-btn"
                onClick={loadRandomImages}
                disabled={loading}
              >
                ÈöèÊú∫ÂõæÁâá
              </button>
            </form>

            {error && <div className="error-message">{error}</div>}

            <div className="images-grid">
              {loading ? (
                <div className="loading">Âä†ËΩΩ‰∏≠...</div>
              ) : (
                images.map(image => (
                  <div key={image.id} className="image-item">
                    <img 
                      src={image.thumb} 
                      alt={image.description}
                      onClick={() => onImageSelect(image.url)}
                    />
                    <div className="image-actions">
                      <button 
                        className="use-btn"
                        onClick={() => onImageSelect(image.url)}
                      >
                        ‰ΩøÁî®
                      </button>
                      <button 
                        className="save-btn"
                        onClick={() => onImageSave(image)}
                      >
                        Êî∂Ëóè
                      </button>
                      <button 
                        className="bg-btn"
                        onClick={() => onSetAsBackground && onSetAsBackground(image.url)}
                      >
                        ËÉåÊôØ
                      </button>
                    </div>
                    <div className="image-info">
                      <span className="author">by {image.author}</span>
                    </div>
                  </div>
                ))
              )}
            </div>
          </div>
        )}

        {activeTab === 'saved' && (
          <div className="saved-section">
            {savedImages.length === 0 ? (
              <div className="empty-saved">
                <div className="empty-icon">üñºÔ∏è</div>
                <p>ËøòÊ≤°Êúâ‰øùÂ≠ò‰ªª‰ΩïÂõæÁâá</p>
                <p>Âú®ÊêúÁ¥¢Ê†áÁ≠æÈ°µ‰∏≠Êî∂ËóèÂñúÊ¨¢ÁöÑÂõæÁâáÂêßÔºÅ</p>
              </div>
            ) : (
              <div className="images-grid">
                {savedImages.map(image => (
                  <div key={image.id} className="image-item">
                    <img 
                      src={image.url} 
                      alt={image.description}
                      onClick={() => onImageSelect(image.url)}
                    />
                    <div className="image-actions">
                      <button 
                        className="use-btn"
                        onClick={() => onImageSelect(image.url)}
                      >
                        ‰ΩøÁî®
                      </button>
                      <button 
                        className="bg-btn"
                        onClick={() => onSetAsBackground && onSetAsBackground(image.url)}
                      >
                        ËÉåÊôØ
                      </button>
                      <button 
                        className="delete-btn"
                        onClick={() => {
                          storageService.deleteDailyImage(date, image.id);
                          // Ëß¶ÂèëÁà∂ÁªÑ‰ª∂Âà∑Êñ∞
                          onImageSave(image);
                        }}
                      >
                        Âà†Èô§
                      </button>
                    </div>
                    <div className="image-info">
                      <span className="author">by {image.author}</span>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default ImageSearchModal;
