import React, { useState, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import moment from 'moment';
import './DateDetailPage.css';

const DateDetailPage = () => {
  const { date } = useParams();
  const navigate = useNavigate();
  const fileInputRef = useRef(null);

  const [content, setContent] = useState({
    text: '',
    images: [],
    backgroundColor: '#ffffff',
    events: []
  });
const [isTouching, setIsTouching] = useState(false);

// Âú®ÁªÑ‰ª∂ËøîÂõûÁöÑJSX‰∏≠Ôºå‰∏∫ÊåâÈíÆÊ∑ªÂä†Ëß¶Êë∏‰∫ã‰ª∂
const buttonProps = {
  onTouchStart: () => setIsTouching(true),
  onTouchEnd: () => setIsTouching(false),
  onMouseDown: () => setIsTouching(true),
  onMouseUp: () => setIsTouching(false),
  onMouseLeave: () => setIsTouching(false),
  style: isTouching ? { transform: 'scale(0.95)' } : {}
};
  // Â§ÑÁêÜÊñáÊú¨ÂÜÖÂÆπÂèòÂåñ
  const handleTextChange = (e) => {
    setContent(prev => ({
      ...prev,
      text: e.target.value
    }));
  };

  // Â§ÑÁêÜÂõæÁâá‰∏ä‰º†
  const handleImageUpload = (e) => {
    const files = Array.from(e.target.files);
    const imageUrls = files.map(file => URL.createObjectURL(file));

    setContent(prev => ({
      ...prev,
      images: [...prev.images, ...imageUrls]
    }));
  };

  // Âà†Èô§ÂõæÁâá
  const handleRemoveImage = (index) => {
    setContent(prev => ({
      ...prev,
      images: prev.images.filter((_, i) => i !== index)
    }));
  };

  // Â§ÑÁêÜËÉåÊôØÈ¢úËâ≤ÂèòÂåñ
  const handleBackgroundColorChange = (e) => {
    setContent(prev => ({
      ...prev,
      backgroundColor: e.target.value
    }));
  };

  // Ê∑ªÂä†Êñ∞‰∫ã‰ª∂
  const handleAddEvent = () => {
    const title = prompt('ËØ∑ËæìÂÖ•‰∫ã‰ª∂Ê†áÈ¢ò:');
    if (title) {
      const newEvent = {
        id: Date.now(),
        title,
        time: moment().format('HH:mm'),
        completed: false
      };

      setContent(prev => ({
        ...prev,
        events: [...prev.events, newEvent]
      }));
    }
  };

  // ÂàáÊç¢‰∫ã‰ª∂ÂÆåÊàêÁä∂ÊÄÅ
  const handleToggleEvent = (id) => {
    setContent(prev => ({
      ...prev,
      events: prev.events.map(event =>
        event.id === id ? { ...event, completed: !event.completed } : event
      )
    }));
  };

  // ‰øùÂ≠òÂÜÖÂÆπÂà∞Êú¨Âú∞Â≠òÂÇ®
  const saveContent = () => {
    localStorage.setItem(`date-content-${date}`, JSON.stringify(content));
    alert('ÂÜÖÂÆπÂ∑≤‰øùÂ≠òÔºÅ');
  };

  // Âä†ËΩΩ‰øùÂ≠òÁöÑÂÜÖÂÆπ
  const loadSavedContent = () => {
    const saved = localStorage.getItem(`date-content-${date}`);
    if (saved) {
      setContent(JSON.parse(saved));
    }
  };

  // È°µÈù¢Âä†ËΩΩÊó∂ËØªÂèñ‰øùÂ≠òÁöÑÂÜÖÂÆπ
  React.useEffect(() => {
    loadSavedContent();
  }, [date]);

  return (
    <div className="date-detail-page" style={{ backgroundColor: content.backgroundColor }}>
      <div className="detail-container">
        {/* Â§¥ÈÉ® */}
        <div className="detail-header">
          <button className="back-btn" onClick={() => navigate('/')}>
            ‚Üê ËøîÂõûÊó•ÂéÜ
          </button>
          <h1 className="detail-title">
            {moment(date).format('YYYYÂπ¥MMÊúàDDÊó•')}
          </h1>
          <button className="save-btn" onClick={saveContent}>
            ‰øùÂ≠ò
          </button>
        </div>

        <div className="detail-content">
          {/* Â∑¶‰æßÔºöÁºñËæëÂå∫Âüü */}
          <div className="edit-section">
            {/* ËÉåÊôØÈ¢úËâ≤ÈÄâÊã© */}
            <div className="control-group">
              <label>ËÉåÊôØÈ¢úËâ≤:</label>
              <input
                type="color"
                value={content.backgroundColor}
                onChange={handleBackgroundColorChange}
              />
            </div>

            {/* ÊñáÊú¨ÁºñËæëÂô® */}
            <div className="control-group">
              <label>Êó•ËÆ∞ÂÜÖÂÆπ:</label>
              <textarea
                value={content.text}
                onChange={handleTextChange}
                placeholder="ÂÜô‰∏ã‰ªäÂ§©ÁöÑÂøÉÊÉÖÂíåËÆ°Âàí..."
                rows="8"
              />
            </div>

            {/* ÂõæÁâá‰∏ä‰º† */}
            <div className="control-group">
              <label>‰∏ä‰º†ÂõæÁâá:</label>
              <input
                type="file"
                ref={fileInputRef}
                onChange={handleImageUpload}
                accept="image/*"
                multiple
                style={{ display: 'none' }}
              />
              <button 
                className="upload-btn"
                onClick={() => fileInputRef.current?.click()}
              >
                üì∑ ÈÄâÊã©ÂõæÁâá
              </button>
            </div>

            {/* ‰∫ã‰ª∂ÁÆ°ÁêÜ */}
            <div className="control-group">
              <label>‰ªäÊó•‰∫ã‰ª∂:</label>
              <button className="add-event-btn" onClick={handleAddEvent}>
                + Ê∑ªÂä†‰∫ã‰ª∂
              </button>

              <div className="events-list">
                {content.events.map(event => (
                  <div key={event.id} className="event-item">
                    <input
                      type="checkbox"
                      checked={event.completed}
                      onChange={() => handleToggleEvent(event.id)}
                    />
                    <span className={event.completed ? 'completed' : ''}>
                      {event.title} - {event.time}
                    </span>
                  </div>
                ))}
              </div>
            </div>
          </div>

          {/* Âè≥‰æßÔºöÈ¢ÑËßàÂå∫Âüü */}
          <div className="preview-section">
            {/* ÂõæÁâáÈ¢ÑËßà */}
            {content.images.length > 0 && (
              <div className="images-preview">
                <h3>ÂõæÁâáÈ¢ÑËßà</h3>
                <div className="images-grid">
                  {content.images.map((url, index) => (
                    <div key={index} className="image-item">
                      <img src={url} alt={`‰∏ä‰º†ÁöÑÂõæÁâá ${index + 1}`} />
                      <button 
                        className="remove-image"
                        onClick={() => handleRemoveImage(index)}
                      >
                        √ó
                      </button>
                    </div>
                  ))}
                </div>
              </div>
            )}

            {/* ÊñáÊú¨È¢ÑËßà */}
            {content.text && (
              <div className="text-preview">
                <h3>Êó•ËÆ∞È¢ÑËßà</h3>
                <div className="preview-text">
                  {content.text}
                </div>
              </div>
            )}

            {/* ‰∫ã‰ª∂È¢ÑËßà */}
            {content.events.length > 0 && (
              <div className="events-preview">
                <h3>‰∫ã‰ª∂ÂàóË°®</h3>
                <div className="preview-events">
                  {content.events.map(event => (
                    <div key={event.id} className={`preview-event ${event.completed ? 'completed' : ''}`}>
                      <span className="event-time">{event.time}</span>
                      <span className="event-title">{event.title}</span>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default DateDetailPage;
