// src/components/EventDetailPage.jsx 
import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import moment from 'moment';
import './EventDetailPage.css';
import { storageService } from '../utils/storage';
import ImageSearchModal from './ImageSearchModal';

const EventDetailPage = () => {
  const { date } = useParams();
  const navigate = useNavigate();
  const [content, setContent] = useState('');
  const [backgroundImage, setBackgroundImage] = useState('');
  const [events, setEvents] = useState([]);
  const [showImageUpload, setShowImageUpload] = useState(false);
  const [showImageSearch, setShowImageSearch] = useState(false);
  const editorRef = useRef(null);
  const isComposingRef = useRef(false);
  const isInitializedRef = useRef(false);

  // Âä†ËΩΩËØ•Êó•ÊúüÁöÑ‰∫ã‰ª∂Êï∞ÊçÆ
  useEffect(() => {
    console.log('üîç Âä†ËΩΩÊï∞ÊçÆÔºåÊó•Êúü:', date);

    const savedContent = storageService.getDailyContent(date);
    const savedBackground = storageService.getDailyBackground(date);
    const dateEvents = storageService.getEventsByDate(date);

    console.log('üìù Âä†ËΩΩÂà∞ÁöÑÂÜÖÂÆπ:', savedContent ? `ÊúâÂÜÖÂÆπÔºåÈïøÂ∫¶: ${savedContent.length}` : 'Êó†ÂÜÖÂÆπ');
    console.log('üñºÔ∏è ËÉåÊôØÂõæÁâá:', savedBackground ? 'Êúâ' : 'Êó†');
    console.log('üìÖ ‰∫ã‰ª∂Êï∞Èáè:', dateEvents.length);

    setContent(savedContent || '');
    setBackgroundImage(savedBackground || '');
    setEvents(dateEvents);

    isInitializedRef.current = true;
  }, [date]);

  // ÂàùÂßãÂåñÁºñËæëÂô®ÂÜÖÂÆπ
  useEffect(() => {
    if (!editorRef.current) return;

    console.log('üéØ ÂàùÂßãÂåñÁºñËæëÂô®ÔºåÂΩìÂâçÂÜÖÂÆπÁä∂ÊÄÅ:', content ? `ÊúâÂÜÖÂÆπÔºåÈïøÂ∫¶: ${content.length}` : 'Á©∫');

    const selection = window.getSelection();
    const range = selection.rangeCount > 0 ? selection.getRangeAt(0) : null;
    const currentFocus = document.activeElement;

    editorRef.current.innerHTML = content || '';

    console.log('‚úÖ ÁºñËæëÂô®ÂÜÖÂÆπÂ∑≤ËÆæÁΩÆ:', editorRef.current.innerHTML.substring(0, 100));

    if (range && currentFocus === editorRef.current) {
      selection.removeAllRanges();
      selection.addRange(range);
    }

    updateEmptyState();

  }, [content, date]);

  const updateEmptyState = () => {
    if (editorRef.current) {
      const html = editorRef.current.innerHTML;
      const isEmpty = !html || 
                     html === '<br>' || 
                     html === '<div><br></div>' || 
                     html === '&#8203;' ||
                     html === '<div>&#8203;</div>';

      if (isEmpty) {
        editorRef.current.classList.add('empty');
        console.log('üì≠ ÁºñËæëÂô®Áä∂ÊÄÅ: Á©∫');
      } else {
        editorRef.current.classList.remove('empty');
        console.log('üì¨ ÁºñËæëÂô®Áä∂ÊÄÅ: ÊúâÂÜÖÂÆπ');
      }
    }
  };

  // ‰ªé DOM Êõ¥Êñ∞ÂÜÖÂÆπÁä∂ÊÄÅ
  const updateContentFromDOM = () => {
    if (editorRef.current) {
      const newContent = editorRef.current.innerHTML;

      const cleanContent = newContent.replace(/&#8203;|<\/?div[^>]*>|<\/?br[^>]*>/g, '').trim();

      console.log('üíæ ÂáÜÂ§á‰øùÂ≠òÂÜÖÂÆπÔºåÈïøÂ∫¶:', newContent.length, 'Ê∏ÖÁêÜÂêé:', cleanContent.length);

      if (cleanContent) {
        setContent(newContent);
        updateEmptyState();
        const saveResult = storageService.saveDailyContent(date, newContent);
        console.log('üíΩ ‰øùÂ≠òÁªìÊûú:', saveResult ? 'ÊàêÂäü' : 'Â§±Ë¥•', 'ÈîÆÂêç:', `daily-content-${date}`);
      } else {
        setContent('');
        updateEmptyState();
        storageService.saveDailyContent(date, '');
        console.log('üíΩ ‰øùÂ≠òÁ©∫ÂÜÖÂÆπ');
      }
    }
  };

  // ÂÜÖÂÆπÂèòÂåñÂ§ÑÁêÜ
  const handleContentChange = () => {
    if (isComposingRef.current) return;

    console.log('‚å®Ô∏è ÂÜÖÂÆπÂèòÂåñËß¶Âèë');
    updateContentFromDOM();
  };

  const handleCompositionStart = () => {
    isComposingRef.current = true;
    console.log('üá®üá≥ ‰∏≠ÊñáËæìÂÖ•ÂºÄÂßã');
  };

  const handleCompositionEnd = () => {
    isComposingRef.current = false;
    console.log('üá®üá≥ ‰∏≠ÊñáËæìÂÖ•ÁªìÊùü');
    updateContentFromDOM();
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      document.execCommand('insertLineBreak');
      setTimeout(updateContentFromDOM, 10);
    }
  };

  const saveContent = () => {
    updateContentFromDOM();
    alert('ÂÜÖÂÆπÂ∑≤‰øùÂ≠òÔºÅ');
  };

  const handleEditorFocus = () => {
    if (editorRef.current) {
      editorRef.current.classList.remove('empty');
      console.log('üéØ ÁºñËæëÂô®Ëé∑ÂæóÁÑ¶ÁÇπ');

      if (editorRef.current.innerHTML === '&#8203;' || editorRef.current.innerHTML === '<div>&#8203;</div>') {
        editorRef.current.innerHTML = '';
        const range = document.createRange();
        const selection = window.getSelection();
        range.selectNodeContents(editorRef.current);
        range.collapse(false);
        selection.removeAllRanges();
        selection.addRange(range);
      }
    }
  };

  const handleEditorBlur = () => {
    console.log('üëã ÁºñËæëÂô®Â§±ÂéªÁÑ¶ÁÇπ');
    updateEmptyState();
    updateContentFromDOM();
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      if (!file.type.startsWith('image/')) {
        alert('ËØ∑ÈÄâÊã©ÂõæÁâáÊñá‰ª∂');
        return;
      }

      if (file.size > 5 * 1024 * 1024) {
        alert('ÂõæÁâáÂ§ßÂ∞è‰∏çËÉΩË∂ÖËøá 5MB');
        return;
      }

      const reader = new FileReader();
      reader.onloadend = () => {
        const imageUrl = reader.result;
        console.log('üñºÔ∏è ÂõæÁâá‰∏ä‰º†ÂÆåÊàêÔºåÂ§ßÂ∞è:', imageUrl.length);

        if (editorRef.current) {
          const selection = window.getSelection();
          const range = selection.rangeCount > 0 ? selection.getRangeAt(0) : null;

          const img = document.createElement('img');
          img.src = imageUrl;
          img.alt = "uploaded";
          img.className = "uploaded-image";

          if (range) {
            range.insertNode(img);
            const space = document.createTextNode(' ');
            range.insertNode(space);
            range.collapse(false);
          } else {
            editorRef.current.appendChild(img);
            editorRef.current.appendChild(document.createTextNode(' '));
          }

          console.log('‚úÖ ÂõæÁâáÊèíÂÖ•ÂÆåÊàê');
          updateContentFromDOM();
        }
      };

      reader.onerror = () => {
        alert('ÂõæÁâáËØªÂèñÂ§±Ë¥•ÔºåËØ∑ÈáçËØï');
      };

      reader.readAsDataURL(file);
    }
    e.target.value = '';
  };

  const handleBackgroundUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      if (!file.type.startsWith('image/')) {
        alert('ËØ∑ÈÄâÊã©ÂõæÁâáÊñá‰ª∂');
        return;
      }

      if (file.size > 5 * 1024 * 1024) {
        alert('ÂõæÁâáÂ§ßÂ∞è‰∏çËÉΩË∂ÖËøá 5MB');
        return;
      }

      const reader = new FileReader();
      reader.onloadend = () => {
        const imageUrl = reader.result;
        setBackgroundImage(imageUrl);
        storageService.saveDailyBackground(date, imageUrl);
        console.log('üé® ËÉåÊôØÂõæÁâáÂ∑≤‰øùÂ≠ò');
      };

      reader.onerror = () => {
        alert('ËÉåÊôØÂõæÁâáËØªÂèñÂ§±Ë¥•ÔºåËØ∑ÈáçËØï');
      };

      reader.readAsDataURL(file);
    }
    e.target.value = '';
  };

  // ÂõæÁâáÊêúÁ¥¢Áõ∏ÂÖ≥ÂäüËÉΩ
  const handleImageSelect = (imageUrl) => {
    if (editorRef.current) {
      const selection = window.getSelection();
      const range = selection.rangeCount > 0 ? selection.getRangeAt(0) : null;

      const img = document.createElement('img');
      img.src = imageUrl;
      img.alt = "unsplash image";
      img.className = "uploaded-image";

      if (range) {
        range.insertNode(img);
        const space = document.createTextNode(' ');
        range.insertNode(space);
        range.collapse(false);
      } else {
        editorRef.current.appendChild(img);
        editorRef.current.appendChild(document.createTextNode(' '));
      }

      updateContentFromDOM();
    }
  };

  const handleImageSave = (imageData) => {
    storageService.saveDailyImage(date, imageData);
    alert('ÂõæÁâáÂ∑≤‰øùÂ≠òÂà∞Êî∂ËóèÔºÅ');
  };

  const handleSetAsBackground = (imageUrl) => {
    setBackgroundImage(imageUrl);
    storageService.saveDailyBackground(date, imageUrl);
    alert('Â∑≤ËÆæÁΩÆ‰∏∫ËÉåÊôØÂõæÁâáÔºÅ');
  };

  const addNewEvent = () => {
    const title = prompt('ËØ∑ËæìÂÖ•‰∫ã‰ª∂Ê†áÈ¢ò:');
    if (!title || title.trim() === '') {
      alert('‰∫ã‰ª∂Ê†áÈ¢ò‰∏çËÉΩ‰∏∫Á©∫');
      return;
    }

    const type = prompt('ËØ∑ËæìÂÖ•‰∫ã‰ª∂Á±ªÂûã (work/meeting/personal/urgent):', 'work');
    const validTypes = ['work', 'meeting', 'personal', 'urgent'];
    if (!validTypes.includes(type)) {
      alert('‰∫ã‰ª∂Á±ªÂûãÂøÖÈ°ªÊòØ: work, meeting, personal Êàñ urgent');
      return;
    }

    const startTime = prompt('ËØ∑ËæìÂÖ•ÂºÄÂßãÊó∂Èó¥ (HH:mm)', '09:00');
    const endTime = prompt('ËØ∑ËæìÂÖ•ÁªìÊùüÊó∂Èó¥ (HH:mm)', '10:00');

    const timeRegex = /^([01]?[0-9]|2[0-3]):[0-5][0-9]$/;
    if (!startTime || !endTime || !timeRegex.test(startTime) || !timeRegex.test(endTime)) {
      alert('Êó∂Èó¥Ê†ºÂºè‰∏çÊ≠£Á°ÆÔºåËØ∑‰ΩøÁî® HH:mm Ê†ºÂºè (00:00 - 23:59)');
      return;
    }

    const start = new Date(date + 'T' + startTime + ':00');
    const end = new Date(date + 'T' + endTime + ':00');

    if (end <= start) {
      alert('ÁªìÊùüÊó∂Èó¥ÂøÖÈ°ªÊôö‰∫éÂºÄÂßãÊó∂Èó¥');
      return;
    }

    const hasConflict = events.some(event => {
      const eventStart = new Date(event.start);
      const eventEnd = new Date(event.end);
      return (start < eventEnd && end > eventStart);
    });

    if (hasConflict) {
      if (!confirm('ËØ•Êó∂Èó¥ÊÆµ‰∏éÂ∑≤Êúâ‰∫ã‰ª∂ÂÜ≤Á™ÅÔºåÊòØÂê¶ÁªßÁª≠Ê∑ªÂä†Ôºü')) {
        return;
      }
    }

    const newEvent = {
      title: title.trim(),
      start: start,
      end: end,
      type: type
    };

    const savedEvent = storageService.addEvent(newEvent);
    setEvents(prev => [...prev, savedEvent]);
    alert('‰∫ã‰ª∂Ê∑ªÂä†ÊàêÂäüÔºÅ');
  };

  const deleteEvent = (eventId) => {
    if (window.confirm('Á°ÆÂÆöË¶ÅÂà†Èô§Ëøô‰∏™‰∫ã‰ª∂ÂêóÔºü')) {
      const updatedEvents = storageService.deleteEvent(eventId);
      setEvents(updatedEvents.filter(event => 
        moment(event.start).isSame(date, 'day')
      ));
      alert('‰∫ã‰ª∂Â∑≤Âà†Èô§');
    }
  };

  const formatEventType = (type) => {
    const typeMap = {
      'work': 'üíº Â∑•‰Ωú',
      'meeting': 'üë• ‰ºöËÆÆ',
      'personal': 'üë§ ‰∏™‰∫∫',
      'urgent': 'üö® Á¥ßÊÄ•'
    };
    return typeMap[type] || type;
  };

  const getEventTypeColor = (type) => {
    const colorMap = {
      'work': '#3498db',
      'meeting': '#9b59b6',
      'personal': '#2ecc71',
      'urgent': '#e74c3c'
    };
    return colorMap[type] || '#3498db';
  };

  // Ë∞ÉËØïÂáΩÊï∞
  const checkStorage = () => {
    console.log('üîç Â≠òÂÇ®Áä∂ÊÄÅÊ£ÄÊü•:');
    console.log('ÂΩìÂâçÊó•Êúü:', date);
    console.log('Â≠òÂÇ®ÈîÆ:', `daily-content-${date}`);
    console.log('Â≠òÂÇ®ÂÜÖÂÆπ:', localStorage.getItem(`daily-content-${date}`));
    console.log('ReactÁä∂ÊÄÅÂÜÖÂÆπ:', content ? `ÊúâÂÜÖÂÆπÔºåÈïøÂ∫¶: ${content.length}` : 'Á©∫');
    console.log('ÁºñËæëÂô®ÂÜÖÂÆπ:', editorRef.current?.innerHTML.substring(0, 200));
  };

  // Âú®ÁªÑ‰ª∂ÊåÇËΩΩÊó∂Ê£ÄÊü•Â≠òÂÇ®
  useEffect(() => {
    console.log('üöÄ EventDetailPage ÁªÑ‰ª∂ÊåÇËΩΩ');
    checkStorage();
  }, []);

  return (
    <div className="event-detail-page">
      <div className="detail-header">
        <button className="back-btn" onClick={() => navigate('/')}>
          ‚Üê ËøîÂõûÊó•ÂéÜ
        </button>
        <h1 className="detail-title">{moment(date).format('YYYYÂπ¥MMÊúàDDÊó• dddd')}</h1>
        <div style={{ display: 'flex', gap: '10px', alignItems: 'center' }}>
          <button 
            className="toolbar-btn" 
            onClick={checkStorage}
            style={{ background: '#6c757d', fontSize: '12px', padding: '5px 10px' }}
          >
            üîç Ë∞ÉËØï
          </button>
          <button className="save-btn" onClick={saveContent}>
            üíæ ‰øùÂ≠òÂÜÖÂÆπ
          </button>
        </div>
      </div>

      <div className="detail-content">
        <div className="editor-toolbar">
          <button 
            className="toolbar-btn" 
            onClick={() => setShowImageUpload(!showImageUpload)}
          >
            üì∑ Ê∑ªÂä†ÂõæÁâá
          </button>

          <button 
            className="toolbar-btn"
            onClick={() => setShowImageSearch(true)}
            style={{ background: '#9b59b6' }}
          >
            üñºÔ∏è ÊêúÁ¥¢ÂõæÁâá
          </button>

          <label className="toolbar-btn">
            üé® ËÉåÊôØÂõæÁâá
            <input 
              type="file" 
              accept="image/*" 
              onChange={handleBackgroundUpload}
              style={{ display: 'none' }}
            />
          </label>

          <button className="toolbar-btn" onClick={addNewEvent}>
            ‚ûï Ê∑ªÂä†‰∫ã‰ª∂
          </button>

          {showImageUpload && (
            <div className="image-upload-panel">
              <span>ÈÄâÊã©ÂõæÁâáÔºö</span>
              <input 
                type="file" 
                accept="image/*" 
                onChange={handleImageUpload}
              />
            </div>
          )}
        </div>

        <div className="events-section">
          <h3>üìÖ ‰ªäÊó•‰∫ã‰ª∂ ({events.length})</h3>
          {events.length > 0 ? (
            <div className="events-list">
              {events.map(event => (
                <div 
                  key={event.id} 
                  className="event-card"
                  style={{ borderLeftColor: getEventTypeColor(event.type) }}
                >
                  <div>
                    <h4>{event.title}</h4>
                    <p>
                      {moment(event.start).format('HH:mm')} - {moment(event.end).format('HH:mm')}
                    </p>
                  </div>
                  <div className="event-actions">
                    <span 
                      className="event-type"
                      style={{ backgroundColor: getEventTypeColor(event.type) }}
                    >
                      {formatEventType(event.type)}
                    </span>
                    <button 
                      className="delete-event-btn"
                      onClick={() => deleteEvent(event.id)}
                    >
                      Âà†Èô§
                    </button>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <p className="no-events">ÊöÇÊó†‰∫ã‰ª∂ÔºåÁÇπÂáª"Ê∑ªÂä†‰∫ã‰ª∂"ÊåâÈíÆÂàõÂª∫Êñ∞‰∫ã‰ª∂</p>
          )}
        </div>

        <div 
          ref={editorRef}
          className="content-editor"
          contentEditable
          suppressContentEditableWarning
          onInput={handleContentChange}
          onFocus={handleEditorFocus}
          onBlur={handleEditorBlur}
          onKeyDown={handleKeyDown}
          onCompositionStart={handleCompositionStart}
          onCompositionEnd={handleCompositionEnd}
          style={{ 
            backgroundImage: backgroundImage ? `url(${backgroundImage})` : 'none',
            backgroundSize: 'cover',
            backgroundPosition: 'center',
            backgroundColor: backgroundImage ? 'rgba(255, 255, 255, 0.9)' : 'transparent'
          }}
        />
      </div>

      {/* ÂõæÁâáÊêúÁ¥¢Ê®°ÊÄÅÊ°Ü */}
      {showImageSearch && (
        <ImageSearchModal
          date={date}
          onClose={() => setShowImageSearch(false)}
          onImageSelect={handleImageSelect}
          onImageSave={handleImageSave}
          onSetAsBackground={handleSetAsBackground}
        />
      )}
    </div>
  );
};

export default EventDetailPage;
